import Sdata from './Sdata';
var Card;
Sdata.map(Card =>{
    return (
        <div className="wrapper">
        <div className="cards">
        <div className="card">
            <div className="img">
                <img src={Sdata.mgsrc} alt="myPic" className="card__img"/>
            </div>
            <div className="card__info">
                <span className="card__category">{Sdata.title}</span>
                <h3 className="card__title"> {Sdata.sname} </h3>
                <a href={props.link} target="_blank">
                    <button>Watch Now</button>
                </a>
            </div>
        </div>
    </div>
    </div>
    )
})
